/**
 * Sets focus to the DOM element specified by its ID.
 * @param {string} id - The ID of the DOM element to focus.
 */
const setFieldFocus = (id) => {
    const element = document.getElementById(id);
    if (element) {
        element.focus();
    } else {
        console.log(`Element with ID ${id} not found.`);
    }
};

/**
 * Initializes the application by setting the focus to a specific field.
 */
const start = () => {
    setFieldFocus("passcode");
    createChangeObserver("resendMessage");
};

const createChangeObserver = (id) => {
    const targetNode = document.getElementById(id);

    if (targetNode) {
        // Observer options
        const config = { childList: true, characterData: true, subtree: true };

        const callback = function (mutationsList, observer) {
            for (let mutation of mutationsList) {
                if (mutation.type === 'characterData' || (mutation.type === 'childList' && mutation.addedNodes.length > 0)) {
                    // Form has reloaded, set focus
                    setFieldFocus("passcode");
                }
            }
        };

        const observer = new MutationObserver(callback);

        observer.observe(targetNode, config);
    }
}

// Ensure the script runs after the DOM is fully loaded
if (document.readyState === "loading") {
    document.addEventListener("DOMContentLoaded", start);
} else {
    start();
}

