module.exports = a = async ({ params }) => {
    const details = params.details;

    // Check if details is set, otherwise return an empty string
    if (!details || details.length === 0) {
        return { 'message': '' };
    }

    const secondsUntilUnlock = details[0]?.rawResponse?.details?.[0]?.innerError?.secondsUntilUnlock ?? null;

    // If secondsUntilUnlock is not available or greater than 100,000 seconds, return an empty string
    const formattedTime = (secondsUntilUnlock !== null && secondsUntilUnlock <= 100000) 
        ? formatTime(secondsUntilUnlock) 
        : null;

    // Return the unlock message or an empty string
    const message = formattedTime ? `Your account will unlock in ${formattedTime}.` : '';

    return { 'message': message };
}

// Function to format time into a human-readable string
const formatTime = (seconds) => {
    const days = Math.floor(seconds / (24 * 3600));
    const hours = Math.floor((seconds % (24 * 3600)) / 3600);
    const minutes = Math.ceil((seconds % 3600) / 60);

    const parts = [];
    if (days > 0) parts.push(`${days} day${days > 1 ? 's' : ''}`);
    if (hours > 0) parts.push(`${hours} hour${hours > 1 ? 's' : ''}`);
    if (minutes > 0) parts.push(`${minutes} minute${minutes > 1 ? 's' : ''}`);

    return parts.join(', ');
}

