customScript.value:
const setFieldFocus = (id) => {
    const element = document.getElementById(id);
    if (element) {
        element.focus();
    }
}

const makePasswordToggle = (id) => {
    let container = document.getElementById(id);

    if (container) {
        let password = container.getElementsByTagName("input")[0];
        let toggler = document.createElement("button");
        toggler.setAttribute("type", "button");
        toggler.setAttribute("aria-label", "Show/Hide Password");
        toggler.className = "btn mdi mdi-eye-off-outline position-absolute end-0 top-50 translate-middle-y";
        container.appendChild(toggler);

        function showHidePassword() {
            if (password.type == "password") {
                password.setAttribute("type", "text");
                toggler.classList.add("mdi-eye-outline");
                toggler.classList.remove("mdi-eye-off-outline");
            } else {
                toggler.classList.add("mdi-eye-off-outline");
                toggler.classList.remove("mdi-eye-outline");
                password.setAttribute("type", "password");
            }
            password.focus();
        };

        toggler.addEventListener("click", showHidePassword);
    }
}

const createChangeObserver = (id) => {
    const targetNode = document.getElementById(id);

    if (targetNode) {
        // Observer options
        const config = { childList: true, characterData: true, subtree: true };

        const callback = function (mutationsList, observer) {
            for (let mutation of mutationsList) {
                if (mutation.type === 'characterData' || (mutation.type === 'childList' && mutation.addedNodes.length > 0)) {
                    // Form has reloaded, recreate the password toggle icons
                    makePasswordToggle("newPasswordContainer");
                    makePasswordToggle("verifyPasswordContainer");
                    setFieldFocus("recoveryCode");
                }
            }
        };

        const observer = new MutationObserver(callback);

        observer.observe(targetNode, config);
    }
}

const start = () => {
    makePasswordToggle("newPasswordContainer");
    makePasswordToggle("verifyPasswordContainer");
    setFieldFocus("recoveryCode");
    createChangeObserver('resendMessage');
}

if (document.readyState === "loading") {
    // Loading hasn't finished yet
    document.addEventListener("DOMContentLoaded", start);
} else {
    // `DOMContentLoaded` has already fired
    start();
}

