module.exports = a = async ({ params }) => {
	function getThemeConfig() {
		const themes = params.themeObject || null;
		const companyId = params.companyInputParam || null;
		const themeId = params.themeId || null;

		if (!themes || themes.length === 0) return null;

		let selectedTheme = null;

		// Try finding by companyId
		if (companyId) {
			const lowerCompanyId = companyId.toLowerCase();
			selectedTheme = themes.find(theme =>
				theme.configuration?.name?.toLowerCase().includes(lowerCompanyId)
			);
		}

		// If not found by companyId, try by themeId
		if (!selectedTheme && themeId) {
			selectedTheme = themes.find(theme => theme.id === themeId);
		}

		// If still not found, use default theme
		if (!selectedTheme) {
			selectedTheme = themes.find(theme => theme.default === true);
		}

		// Return combined object (id + configuration fields)
		if (selectedTheme && selectedTheme.configuration) {
			return {
				id: selectedTheme.id,
				...selectedTheme.configuration
			};
		}

		return null;
	}

	function getThemeDomains() {
		const themes = params.themeObject || null;
		if (!themes || themes.length === 0) return [];

		return themes.map(theme => {
			const name = theme.configuration?.name || '';
			return `@${name.toLowerCase().replace(/\s+/g, '')}.com`;
		});
	}

	return {
		themeObject: getThemeConfig(),
		themeDomain: getThemeDomains()
	};
};

