// Get the element to observe
const rpElem = document.getElementById("riskPayload");

// Log the continueButton element
console.log("Continue Button: ", document.getElementById('continueButton'));

// Function to handle mutations
const callback = (mutationList, observer) => {
  for (const mutation of mutationList) {
    if (mutation.type === 'childList') {
      const continueButton = document.getElementById('continueButton');
      if (continueButton) {
        console.log("Continue Button Clicked");
        continueButton.click();
      } else {
        console.log("Continue Button not found.");
      }
    }
  }
};

// Ensure rpElem is valid before observing
if (rpElem) {
  const observer = new MutationObserver(callback);

  // Start observing the target node for configured mutations
  observer.observe(rpElem, { attributes: true, childList: true, subtree: true });
} else {
  console.log("Element with ID 'riskPayload' not found.");
}

// Set a timeout to click the continueButton after 60 seconds
setTimeout(function() {
  const continueButton = document.getElementById('continueButton');
  if (continueButton) {
    continueButton.click();
  } else {
    console.log("Continue Button not found after timeout.");
  }
}, 600);

