:root {
  --risk-green: #28cc2d;
  --risk-yellow: #ffe135;
  --risk-red: #d82f3e;
  --risk-needle: #424344;
}

.progress {
  width: 300px;
  height: 20px;
  background: linear-gradient(to right, var(--risk-green) 39%, var(--risk-yellow) 39% 75%, var(--risk-red) 75%);
  border-radius: 4px;
  position: relative;
  overflow: visible;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
}

.needle {
  position: absolute;
  top: -8px;
  left: 0%;
  width: 6px;
  height: 36px;
  background-color: var(--risk-needle);
  transition: left 0.5s cubic-bezier(0.42, 0, 0.58, 1);
  border-radius: 3px;
}

.btn-primary {
  background-color: {{global.variables.buttonColor}} !important;
}

.form-control {
  border-color: {{global.variables.buttonColor}} !important;
}

.end-user-nano .bg-light {
  background-color: {{global.variables.backgroundColor}} !important;
}

.end-user-nano .card {
  background-color: {{global.variables.cardColor}} !important;
}

