/*
 Update the devices array to include a new properties 
 used when rendering device list.
*/

// Main entry point
module.exports = a = async ({ params }) => {
	// var allowedTypes = ['SMS', 'EMAIL', 'VOICE'];
	var allowedTypes = JSON.parse(params.allowedBackUpTypes.replace(/'/g, '"'));
	const extractedInfo = extractUserInfo(params.userObject, allowedTypes);
	const devices = [];

	allowedTypes.forEach(type => {
		let device = { type: type };
		switch (type) {
			case SMS:
				device.phone = extractedInfo.sms;
				device.maskedValue = maskPhone(device.phone);
				device.displayText = `Mobile 1${SEPARATOR}${device.maskedValue}`;
				device.displayIcon = deviceTypes[type]?.icon || "";
				device.displayType = deviceTypes[type]?.title || "Unknown";
				device.nickname = "Mobile";
				device.status = "ACTIVE";
				device.id = "SMS"
				break;

			case EMAIL:
				device.email = extractedInfo.email;
				device.maskedValue = maskEmail(device.email);
				device.displayText = `Email ${SEPARATOR}${device.maskedValue}`;
				device.displayIcon = deviceTypes[type]?.icon || "";
				device.displayType = deviceTypes[type]?.title || "Unknown";
				device.nickname = "Email";
				device.status = "ACTIVE";
				device.id = "EMAIL"
				break;

			case VOICE:
				// Assuming voice information is not present in the user object
				device.phone = extractedInfo.sms;
				device.maskedValue = maskPhone(device.phone);
				device.displayText = `Voice ${SEPARATOR}${device.maskedValue}`;
				device.displayIcon = deviceTypes[type]?.icon || "";
				device.displayType = deviceTypes[type]?.title || "Unknown";
				device.nickname = "Voice";
				device.status = "ACTIVE";
				device.id = "VOICE"
				break;

			default:
				device.displayText = "";
				device.displayIcon = "";
				device.displayType = "Unknown";
				device.nickname = "";
				device.status = "INACTIVE";
		}

		device.usableStatus = { status: "ENABLED" };

		devices.push(device);
	});

	return { "devices": devices };
}

// Titles and icons
const deviceTypes = {
	SMS: { title: "Text Message", icon: "mdi-comment-text-outline" },
	VOICE: { title: "Voice", icon: "mdi-face-agent" },
	EMAIL: { title: "Email", icon: "mdi-email-outline" },
}

// Device types that require masking
const SMS = "SMS";
const VOICE = "VOICE";
const EMAIL = "EMAIL";
const SEPARATOR = " - ";

// Mask email
const maskEmail = (email) => {
	const [username, domain] = email.split("@");
	const maskedUsername =
		username.charAt(0) + "*".repeat(username.length - 2) + username.slice(-1);
	return maskedUsername + "@" + domain;
};

//Mask phone number
const maskPhone = (phone) => {
	const numericPhoneNumber = phone.replace(/[^\d+]/g, "");

	// const formattedPhoneNumber = numericPhoneNumber.length === 10 ? '1' + numericPhoneNumber : numericPhoneNumber;

	// Mask the first six digits of the phone number
	const maskedPhoneNumber = "***-***-" + numericPhoneNumber.slice(-4);

	return maskedPhoneNumber;
};

// Function to extract user info based on type for Backup Methods
function extractUserInfo(user, types) {
	console.log("user: ", user)
	console.log("types: ", types)
	const result = {};

	// Helper function to format phone numbers
	const formatPhoneNumber = (phone) => {
		if (!phone) return 'Not Available';

		// Remove hyphens and non-digit characters
		let numericPhoneNumber = phone.replace(/[^\d]/g, "");

		// Add '1' if the phone number has exactly 10 digits
		if (numericPhoneNumber.length === 10) {
			numericPhoneNumber = '1' + numericPhoneNumber;
		}

		return numericPhoneNumber;
	};

	if (types.includes('SMS')) {
		result.sms = formatPhoneNumber(user.mobilePhone);
	}

	if (types.includes('EMAIL')) {
		result.email = user.email || 'Not Available';
	}

	if (types.includes('VOICE')) {
		result.voice = 'Not Available'; // Replace with actual data if available
	}

	return result;
}

