const setFieldFocus = (id) => {
    const element = document.getElementById(id);
    if (element) {
        element.value='';
        element.focus();
    }
}

/**
 * Enables or disables a button based on whether an input field contains text.
 * @param {string} inputId - The ID of the input field to monitor.
 * @param {string} buttonId - The ID of the button to enable/disable.
 */
const toggleButtonState = (inputId1, buttonId) => {
    const passcodeField = document.getElementById(inputId1);
    const button = document.getElementById(buttonId);

    if (passcodeField && button) {
        // Disable the button initially when the DOM is fully loaded
        button.disabled = true;
        // Add input event listener to update the button state dynamically
        passcodeField.addEventListener("input", () => {
	        button.disabled = passcodeField.value.trim().length === 0;
        });
    } else {
        console.log(`Element with ID ${passcodeField} or ${buttonId} not found.`);
    }
};

const start = () => {
    setFieldFocus("passcode");
    toggleButtonState("passcode", "submit"); // monitors the passcode field for input
}

if (document.readyState === "loading") {
    // Loading hasn't finished yet
    document.addEventListener("DOMContentLoaded", start);
} else {
    // `DOMContentLoaded` has already fired
    start();
}

const maxLength = 6;
function checkInputValid(val) {
    if (maxLength === "44") {
        if (/[a-z]{44}/.test(val)) {
            return true;
        }
    } else if (maxLength === "8") {
        if (/^[0-9]{6}$|^[0-9]{8}$/.test(val)) {
            return true;
        }
    } else {
        if (/^[0-9]{6}$/.test(val)) {
            return true;
        }
    }
    return false;
}

