const setFieldFocus = (id) => {
    const element = document.getElementById(id);
    if (element) {
        element.focus();
    }
}

const start = () => {
    setFieldFocus("passcode");
}

if (document.readyState === "loading") {
    // Loading hasn't finished yet
    document.addEventListener("DOMContentLoaded", start);
} else {
    // `DOMContentLoaded` has already fired
    start();
}

const maxLength = 6;
function checkInputValid(val) {
    if (maxLength === "44") {
        if (/[a-z]{44}/.test(val)) {
            return true;
        }
    } else if (maxLength === "8") {
        if (/^[0-9]{6}$|^[0-9]{8}$/.test(val)) {
            return true;
        }
    } else {
        if (/^[0-9]{6}$/.test(val)) {
            return true;
        }
    }
    return false;
}

