customScript.value:
function isWebAuthnSupported() {
    try {
        if (!window.PublicKeyCredential) {
            console.log("Web Authentication API is not supported on this browser.");
            return false;
        }
        return true;
    } catch (error) {
        console.log(error);
        return false;
    }
}

function isWebAuthnPlatformAuthenticatorAvailableV2() {

    getOrginAndRPID(window.location.origin)

    var timer;

    var isTimeout = false;

    var p1 = new Promise(function (resolve) {
        timer = setTimeout(function () {
            console.log("isWebAuthnPlatformAuthenticatorAvailable - Timeout");
            isTimeout = true;
            resolve(false);
        }, 500);
    });

    var p2 = new Promise(function (resolve) {

        if (isWebAuthnSupported() && window.PublicKeyCredential.isUserVerifyingPlatformAuthenticatorAvailable) {

            resolve(
                window.PublicKeyCredential.isUserVerifyingPlatformAuthenticatorAvailable().catch(function (err) {
                    console.log(err);
                    return false;
                }));
        }
        else {
            resolve(false);
        }
    });

    return Promise.race([p1, p2]).then(function (res) {
        clearTimeout(timer);
        console.log("isWebAuthnPlatformAuthenticatorAvailable - " + res);

        return { supported: res, timeout: isTimeout };
    });
}

function getOrginAndRPID(origin) {
    // console.log("START ORIGIN");
    // console.log(origin);

    // const index = origin.indexOf("pingone.");
    // // const index = origin.indexOf("mw.ping-eng.");
    // const rpid = index !== -1 ? origin.substr(index) : "unknown"; // Handle cases where "pingone." is not found

    // // console.log("index", index);
    // // console.log("rpid", rpid);

    // // Update hidden inputs
    // document.getElementById('origin').value = origin;
    // document.getElementById('rpid').value = rpid;

    // Extract the RPID by getting the main domain from the origin
    // This removes any subdomains, like "login.example.com" -> "example.com"
    const urlParts = origin.split('.');
    const rpid = urlParts.length > 2 ? urlParts.slice(-2).join('.') : origin;

    // Update hidden inputs
    const originElement = document.getElementById('origin');
    const rpidElement = document.getElementById('rpid');

    if (originElement) originElement.value = origin;
    if (rpidElement) rpidElement.value = rpid;

    console.log("Origin:", origin);
    console.log("RPID:", rpid);
}

setTimeout(function () {
    const authSuported = isWebAuthnSupported();
    isWebAuthnPlatformAuthenticatorAvailableV2().then(function (result) {
        document.forms['webAuthNSupportForm'].elements["webAuthNSupport"].value = result.supported ? "FULL" : (authSuported ? "SECURITY_KEY_ONLY" : "NONE");
        document.getElementById('autoSubmit').click();
    }).catch(function (err) {
        document.forms['webAuthNSupportForm'].elements["webAuthNSupport"].value = authSuported ? "SECURITY_KEY_ONLY" : "NONE";
        document.getElementById('autoSubmit').click();
    });
}, 200);

