// Titles and icons
const deviceTypes = {
    SMS: { title: "Text Message", icon: "mdi-comment-text-outline" },
    VOICE: { title: "Voice", icon: "mdi-face-agent" },
    EMAIL: { title: "Email", icon: "mdi-email-outline" },
    TOTP: { title: "Authenticator App", icon: "mdi-cellphone-key" },
    FIDO2: { title: "Passkey/FIDO2", icon: "mdi-fingerprint" },
    PLATFORM: { title: "Biometrics", icon: "mdi-fingerprint" },
    MOBILE: { title: "Mobile Push", icon: "mdi-account-key-outline" },
    PINGID_MOBILE: { title: "PingID App", icon: "mdi-cellphone" },
    SECURITY_KEY: { title: "Security Key", icon: "mdi-fingerprint" },
    MAGIC: { title: "Magic Link", icon: "mdi-auto-fix" },
    PINGID_DESKTOP: { title: "Desktop App", icon: "mdi-monitor-account" },
    OATH_TOKEN: { title: "Hardware Token", icon: "mdi-usb-flash-drive" },
    YUBIKEY: { title: "YubiKey", icon: "mdi-usb-flash-drive" }
};

// Main entry point
module.exports = async ({ params }) => {
    // Get Allowed Types as an array
    const allowedTypes = params.devices._embedded.allowedtypes || [];

    // Get mandatory methods as an array
    // const mandatoryMethods = params.mandatoryMethods ? params.mandatoryMethods.split(' ') : [];
    const mandatoryMethods = params.mandatoryMethods ? JSON.parse(params.mandatoryMethods) : [];

    const allowedDevices = params.allowedDevices ? JSON.parse(params.allowedDevices) : [];

    // Get WebAuthN Support as an array
    const webAuthNSupport = params.webAuthNSupport || "FULL"; // e.g., "FULL", "SECURITY_KEY_ONLY", or "NONE"

    // Build initial device list with 'mandatory' flag and 'show' attribute
    let usableDevices = allowedTypes.map(type => ({
        displayText: deviceTypes[type]?.title || "Unknown",
        displayIcon: deviceTypes[type]?.icon || "mdi-help-circle",
        type: type,
        mandatory: mandatoryMethods.includes(type),
        allowed: allowedDevices.includes("ALL") || allowedDevices.includes("MFA") || allowedDevices.includes(type),
        show: true // type !== 'PINGID_MOBILE' // 'show' is false only for 'PINGID_MOBILE'
    }));

    // Apply webAuthNSupport filters
    if (webAuthNSupport === "NONE") {
        usableDevices = usableDevices.filter(device => device.type !== "SECURITY_KEY" && device.type !== "PLATFORM" && device.type !== "FIDO2");
    } else if (webAuthNSupport === "SECURITY_KEY_ONLY") {
        usableDevices = usableDevices.filter(device => device.type !== "PLATFORM");
    }

    // Filter out devices with coolDownExpiresAt set
    usableDevices = usableDevices.filter(device => device.coolDownExpiresAt === undefined);


    return {
        usableDevices: usableDevices
    };
};

