const focusOnFirstInputElement = () => {
  document.getElementById("username").focus();
};

/**
 * If page isn't loaded yet, wait for the page to load, then focus on first
 * input element.
 */
if (document.readyState === "loading") {
  // Loading hasn't finished yet
  document.onreadystatechange = () => {
    if (document.readyState === "complete") {
      focusOnFirstInputElement();
    }
  };
} else {
  // `DOMContentLoaded` has already fired
  focusOnFirstInputElement();
}

function getQueryParam(param) {
    let params = new URLSearchParams(window.location.search);
    return params.get(param);
}

function setQueryField(paramValue, inputId) {
    let inputField = document.getElementById(inputId);
    if (inputField) {
        inputField.value = paramValue;
    } else {
        console.error(`Input field with id "${inputId}" not found.`);
    }
}

function clickContinueButton() {
    const continueButton = document.getElementById('continueButton');
    if (continueButton) {
        console.log("Clicking continue button...");
        continueButton.click();
    } else {
        console.error("Continue Button not found.");
    }
}

// Retrieve the companyId query parameter
let companyId = getQueryParam('companyId') || '';  // Default to empty string if not found
console.log(`Query parameter received: companyId = "${companyId}"`);

// Set the input field value (empty if no query param found)
setQueryField(companyId, 'queryParam');

// Allow time for the field to be populated before clicking
setTimeout(() => {
    clickContinueButton();
}, 250);  // Short delay to ensure field is updated
