document.getElementById('uploadButton').addEventListener('click', function () {
  const files = document.getElementById('fileInput').files;
  const file = files[0];

  if (!file) {
    alert('No file selected!');
    return;
  }

  const fileReader = new FileReader();

  fileReader.onload = function (event) {
    // Show and store file content
    const fileContentDisplay = document.getElementById('fileContent');
    // fileContentDisplay.style.display = 'block';
    // fileContentDisplay.innerText = event.target.result;

    document.getElementById('hiddenFileContent').value = event.target.result;

    // Begin upload to PingOne after file read
    const formData = new FormData();
    formData.append('usageType', 'SIGNING');
    formData.append('file', file, file.name);

    fetch('https://api.pingone.com/v1/environments/4ae5ce1a-2b36-4abd-97b5-844408b2e86c/certificates', {
      method: 'POST',
      headers: {
        'Authorization': 'Bearer {{local.pdgeinsgob.payload.output.access_token}}'
      },
      body: formData
    })
      .then(response => response.json())
      .then(data => {
        console.log('Upload successful:', data);
        alert('Certificate uploaded successfully!');

        const certIdInput = document.getElementById('certId');
        if (certIdInput && data.id) {
          certIdInput.value = data.id;
        } else {
          console.warn('Certificate ID not found in response or hidden input missing');
        }

        // Enable the submit button after upload is successful
        document.getElementById('submitBtn').classList.remove('disabled');
      })
      .catch(error => {
        console.error('Upload to PingOne failed:', error);
        alert('Error uploading certificate to PingOne');
      });
  };

  fileReader.onerror = function () {
    alert('Error reading file!');
  };

  fileReader.readAsText(file);
});

