// All required input field IDs (both manual + discovery)
const requiredFields = [
  'applicationName',
  'clientID',
  'clientSecret',
  'issuer',
  'jwksEndpoint',
  'authEndpoint',
  'tokenEndpoint'
];

let filledByDiscovery = false;

// Reusable function to check if the form is ready
function checkFormReady() {
  const allFilled = requiredFields.every(id => {
    const input = document.getElementById(id);
    return input && input.value.trim() !== '';
  });

  const submitBtn = document.getElementById('submitBtn');
  if (submitBtn) {
    if (allFilled) {
      submitBtn.disabled = false;
      submitBtn.classList.remove('disabled');
    } else {
      submitBtn.disabled = true;
      submitBtn.classList.add('disabled');
    }
  }
}

// Discovery button logic
document.getElementById('uploadButton').addEventListener('click', function () {
  const disDocURIInput = document.getElementById('disDocURI');
  const discoveryUrl = disDocURIInput.value?.trim();

  if (!discoveryUrl) {
    alert('Please enter a Discovery Document URI.');
    return;
  }

  fetch(discoveryUrl)
    .then(response => {
      if (!response.ok) {
        throw new Error(`Failed to fetch discovery document: ${response.status}`);
      }
      return response.json();
    })
    .then(data => {
      console.log('Discovery Document:', data);

      const fieldMap = {
        issuer: 'issuer',
        jwks_uri: 'jwksEndpoint',
        authorization_endpoint: 'authEndpoint',
        token_endpoint: 'tokenEndpoint'
      };

      for (const [discoveryKey, inputId] of Object.entries(fieldMap)) {
        const input = document.getElementById(inputId);
        if (input && data[discoveryKey]) {
          input.value = data[discoveryKey];
        }
      }

      filledByDiscovery = true;
      checkFormReady();

      // alert('Discovery document values loaded successfully!');
    })
    .catch(error => {
      console.error('Error fetching discovery document:', error);
      alert('Failed to load discovery document. Please check the URI and try again.');
    });
});

// Manual input event listeners for all required fields
requiredFields.forEach(id => {
  const input = document.getElementById(id);
  if (input) {
    input.addEventListener('input', () => {
      filledByDiscovery = false;
      checkFormReady();
    });
  }
});

